@Configuration
@EnableWebSecurity
@RequiredArgsConstructor
class WebSecurityConfiguration {

    private static final String[] SWAGGER_PATHS = {&quot;/swagger-ui.html&quot;, &quot;/v3/api-docs/**&quot;, &quot;/swagger-ui/**&quot;, &quot;/webjars/swagger-ui/**&quot;};

    @Bean
