    public static int search(int[] nums, int target) {
        int start = 0;
        int end = nums.length - 1;

        while (start &lt;= end) {
            int mid = start + (end - start) / 2;

            if (nums[mid] == target) {
                return mid;
            }

            if (nums[start] &lt;= nums[mid]) { // Left side is sorted
                if (target &gt;= nums[start] &amp;&amp; target &lt; nums[mid]) {
                    end = mid - 1;
