                    start = mid + 1;
                }
            } else { // Right side is sorted
                if (target &gt; nums[mid] &amp;&amp; target &lt;= nums[end]) {
                    start = mid + 1;
                } else {
                    end = mid - 1;
                }
            }
        }

        return -1;
    }

