public static void createImage(int[] colors, int width, int height) throws IOException {
    BufferedImage image = new BufferedImage(width, height, BufferedImage.TYPE_INT_ARGB);

    // Iterate over array and set image pixel-by-pixel
    for (int i = 0; i &lt; colors.length; i += 4) {
        int row = (i / 4) / width;
        int column = (i / 4) % width;

        int red = colors[i];
        int green = colors[i + 1];
        int blue = colors[i + 2];
        int alpha = colors[i + 3];

        // rgb value is 8 + 8 + 8 + 8 bits, each for r/g/b/a number
        int rgb = (alpha &lt;&lt; 24) | (red &lt;&lt; 16) | (green &lt;&lt; 8) | blue;

        image.setRGB(column, row, rgb);
    }

    ImageIO.write(image, &quot;png&quot;, new File(&quot;result.png&quot;));
}
