        if (moveTo &lt;= 0 || moveTo &gt; lineCounter) {
            moveTo = lineCounter;
        }
            
            
        // Set up to start reading our file from the beginning again.
        reader = new BufferedReader(new FileReader(filePath));
        lineCounter = 0;  // Reset the line counter
            
