        TransmogrifierChained transmogrifier = new TransmogrifierChained(key);

        String message = &quot;Hello World!&quot;;
        System.out.println(&quot;Original message: &quot; + message);

        String encrypted = transmogrifier.encrypt(message);
        System.out.println(&quot;Encrypted message: &quot; + encrypted);

        transmogrifier = new TransmogrifierChained(key); // new instance!
        String decrypted = transmogrifier.decrypt(encrypted);
