import java.util.Random;

public class TransmogrifierChained {
    private Random mixer;
    private int xorValue;

    public TransmogrifierChained(long key) {
        mixer = new Random(key);
        xorValue = mixer.nextInt() &amp; 0x7F;
    }

    public char mutate(char c) {
        int asciiValue = (int) c;
        int mutatedValue = asciiValue ^ xorValue;
        xorValue = mixer.nextInt() &amp; 0x7F; // generate new xor value
        return (char) mutatedValue;
    }

    public String mutate(String message) {
        StringBuilder encrypted = new StringBuilder();
        for (char c : message.toCharArray()) {
            encrypted.append(mutate(c));
        }
        return encrypted.toString();
    }

    public String encrypt(String message) {
        return mutate(message);
    }

    public String decrypt(String message) {
        StringBuilder decrypted = new StringBuilder();
        for (char c : message.toCharArray()) {
            decrypted.append(mutate(c));
        }
        return decrypted.toString();
