        public interface BiFunctionCustom&lt;T, U&gt; extends BiFunction&lt;T,U,U&gt; {
     
          default BiFunctionCustom&lt;T, U&gt; andThen(BiFunctionCustom&lt;T, U&gt; after) {
              Objects.requireNonNull(after);
              return (T t, U u) -&gt; after.apply(t, apply(t, u));
