    public class MaskJsonSerializer extends JsonSerializer&lt;Object&gt; {
        
        private Map&lt;String, MyMaskStrategy&gt; ms;
        
        public MaskJsonSerializer(Map&lt;String, MyMaskStrategy&gt; ms) {
            this.ms=ms;
        }
        
        @Override
        public void serialize(Object value, JsonGenerator gen, SerializerProvider serializers) throws IOException {
            gen.writeStartObject();
            BeanDescription beanDesc = serializers.getConfig().introspect(serializers.constructType(value.getClass()));
            for (BeanPropertyDefinition prop : beanDesc.findProperties()) {
                String propName = prop.getName();
                Object propValue = prop.getAccessor().getValue(value);
                MyMaskStrategy strategy = ms.get(propName);
                if (strategy != null) {
                    propValue = strategy.mask(propValue);
                }
                gen.writeObjectField(propName, propValue);
            }
            gen.writeEndObject();
        }
    }

