		for (int i = 0; i &lt; len; ) {
			int codePoint = str.codePointAt(i);
			int charCount
				= Character.charCount(codePoint);
			
			if (charCount &gt; 1) {
				for (int j = 0; j &lt; charCount; j++)
					sb.append(str.charAt(i + j));
				i += charCount;
				continue;
			}
