    A(B b) {
        this.b = b;
    }

    void aMethod(int x) {
        b.bMethod(x, new Date());
    }
}

class B {
    void bMethod(int x, Date d) {}
}

@ExtendWith(MockitoExtension.class)
public class MyTest {
    @InjectMocks
    private A a;
    @Mock
    private B b;

