    public double calculate(int quant, int val, double sub, double price, TextField text)
    {
        // Calculates the price
        String rounded = &quot;&quot;;
        quant = val;
        sub = price * quant;
        // Rounds the price to the hundredths place
        rounded = twoPlaces.format(sub);
        // Sets the text to the rounded value
        text.setText(rounded);
        // Returns the price (unrounded)
        return sub;
    }

