        // sets all quantity variables to 0

        // Gets the date
        LocalDate date = LocalDate.now();
        // Gets the time
        LocalTime time = LocalTime.now();
        int min = time.getMinute();
        int hr = time.getHour();
        String timeString = &quot;&quot;;
        String PmAm = &quot;&quot;;
        // Checks if time is AM or PM
        // If the time is exactly 12, then there is no need to subtract 12.
        if (hr == 12)
        {
            PmAm = &quot;PM&quot;;
        }
        if (hr &lt; 12)
        {
            PmAm = &quot;AM&quot;;
        }
        // If the time is PM, then subtract 12 (13 in military time (13-12) is 1 in normal time)
        if (hr &gt; 12)
        {
            PmAm = &quot;PM&quot;;
            hr = hr - 12;
        }
        // Adds the : and a Zero if the current time is not a double-digit number.
        if (min &lt; 10)
        {
            timeString = hr + &quot;:0&quot; + min + &quot; &quot; + PmAm;
        } else {
            timeString = hr + &quot;:&quot; + min + &quot; &quot; + PmAm;
        }
        orderTime = timeString;
        // Sets the date label
        dateLabel.setText(date + &quot; | &quot; + timeString);
        // Initializes every value to zero
        onClearOrder();
        nameField.requestFocus();
    }

    public double calculate(int quant, int val, double sub, double price, TextField text)
    {
        // Calculates the price
        String rounded = &quot;&quot;;
        quant = val;
        sub = price * quant;
        // Rounds the price to the hundredths place
        rounded = twoPlaces.format(sub);
        // Sets the text to the rounded value
        text.setText(rounded);
        // Returns the price (unrounded)
        return sub;
    }

