    @FXML
    public void onCalculate()
    {
        // Sets values to 0
        processFee = 0.00;
        orderTotal = 0.00;
        // Calculates the total of all the subtotals
        subtotalTotal = boardSub + cardSub + memorySub + mouseSub + driveSub + monitorSub + desktopSub + laptopSub;
        // Calculates sales tax (rate is 6%)
        salesTax = subtotalTotal * 0.06;
        // adds the total of all subtotals to the just calculated sales tax amount
        orderWithTax = subtotalTotal + salesTax;
        // Checks if the user is a member
        if (!isMember)
        {
            String rounded = &quot;&quot;;
            // If the user is not a member, then the process fee is 1.5%
            orderTotal = orderWithTax;
            processFee = orderWithTax * 0.015;
            orderTotal += processFee;
            // Rounds each value and sets text
            rounded = twoPlaces.format(subtotalTotal);
            subtotalTotals.setText(rounded);
            rounded = twoPlaces.format(salesTax);
            salesTaxes.setText(rounded);
            rounded = twoPlaces.format(orderWithTax);
            orderWithTaxes.setText(rounded);
            rounded = twoPlaces.format(orderTotal);
            orderTotals.setText(rounded);
            rounded = twoPlaces.format(processFee);
            processFees.setText(rounded);
        } else
        {
            // If the user IS a member, then there is no process fee.
            orderTotal = orderWithTax;
            String rounded = &quot;&quot;;
            // Rounds and sets text.
            rounded = twoPlaces.format(subtotalTotal);
            subtotalTotals.setText(rounded);
            rounded = twoPlaces.format(salesTax);
            salesTaxes.setText(rounded);
            rounded = twoPlaces.format(orderWithTax);
            orderWithTaxes.setText(rounded);
            rounded = twoPlaces.format(orderTotal);
            orderTotals.setText(rounded);
            orderTotal = orderWithTax;
            processFee = 0;
            processFees.setText(&quot;0.00&quot;);
        }
        // Initializes profit total to 0
        profitTotal = 0;
        // Calculates the profit total
        profitTotal += boardProfit * boardQuant;
        // adds the rest of the profits to the profit total
    }

