    @FXML
    public void onPrint() throws IOException
    {
        // If the order is the same as the last order, then set the isOld order boolean to true
        if(oldTotal == orderWithTax)
        {
            isOld = true;
        } else {
            isOld = false;
        }
        // If the order is not the same as the old order, then calculate the cumulative values.
        if(!isOld)
        {
            cumulativeProfit += profitTotal;
            cumulativeQuantity += (boardQuant + cardQuant + memoryQuant + mouseQuant + driveQuant + monitorQuant + desktopQuant + laptopQuant);
        }
        // Sets the old total to the current total
        oldTotal = orderWithTax;
        // Rounds and sets text
        String rounded = &quot;&quot;;
        rounded = twoPlaces.format(profitTotal);
        profitTotals.setText(rounded);
        rounded = twoPlaces.format(cumulativeProfit);
        // Sets text for cumulative profits and cumulative quantities.
        cumulativeProfits.setText(rounded);
        cumulativeQuantities.setText(String.valueOf(cumulativeQuantity));
        if(!windowOpened)
        {
            staticStorage.set(boardPrice, cardPrice, selectedMemoryPrice, selectedMousePrice, selectedDrivePrice, monitorPrice, desktopPrice, laptopPrice,
                    boardQuant, cardQuant, memoryQuant, mouseQuant, driveQuant, monitorQuant, desktopQuant, laptopQuant, orderTotal, orderTime, name, address,
                        city, state, zip);
            Scene scene = new Scene(fxmlLoader.load());
            stage2.setScene(scene);
            stage2.setTitle(&quot;Stage 2&quot;);
            stage2.show();
            windowOpened = true;
        }
    }

