public String myFunction(Internal instance) { return myFunctionInternal(instance); }

public String myFunction(Public instance) { return myFunctionInternal(instance); }

public String myFunction(Export instance) { return myFunctionInternal(instance); }

private String myFunctionInternal(Object instance) { ... }

