    public static Map&lt;String, Long&gt; extractAccountInfo(String text) {
        Map&lt;String, Long&gt; result = new HashMap&lt;&gt;();
        Matcher accountMatcher = accountPattern.matcher(text);
        Matcher routingMatcher = routingPattern.matcher(text);

