    public Mono&lt;Response&gt; getShipment(List&lt;String&gt; shipmentOrderNumbers) {
        final String apiUrl = &quot;/some-path&quot;;
        final String queryParam = &quot;some-query-param&quot;;

        ParameterizedTypeReference&lt;List&lt;String&gt;&gt; expectedType = new ParameterizedTypeReference&lt;List&lt;String&gt;&gt;() {};

        return Flux.fromIterable(shipmentOrderNumbers)
                .flatMap(orderNo -&gt; this.client.get()
                        .uri(uriBuilder -&gt; uriBuilder.path(apiUrl).queryParam(queryParam, orderNo).build())
                        .retrieve()
                        .bodyToMono(expectedType)
                        .map(product -&gt; Pair.of(orderNo, product)) // map list to pair of orderNo vs list
                )
                .collectMap(Pair::getFirst, Pair::getSecond) // collect pairs keys and values to map
                .map(Response.Shipment::new) 
