    public static int power(String s) {
        s = s.replaceAll(&quot;^0+&quot;, &quot;&quot;); // Only needed if input is not normalized
        int size = s.length();
        if (size == 0) return 0;
        
        StringBuffer sum = new StringBuffer(size);
        sum.setLength(size);
        // The current power of 2 is encoded in the string buffer 
        //    in a right-aligned manner:
        int start = size - 1;
        sum.setCharAt(start, &#39;1&#39;); 
        
        while (start &gt; 0 || sum.charAt(0) &lt; s.charAt(0)) {
            // Multiply number encoded in stringbuffer by 2;
            int carry = 0;
            for (int i = size - 1; i &gt;= start; i--) {
                int digit = (sum.charAt(i) - &#39;0&#39;) * 2 + carry;
                sum.setCharAt(i, (char) (digit % 10 + &#39;0&#39;));
                carry = digit / 10;
            }
            if (carry &gt; 0) {
                start--;
                if (start &lt; 0) return 0;
                sum.setCharAt(start, &#39;1&#39;);
            }
        }
        return sum.toString().equals(s) ? 1 : 0;
    }
