    import android.net.wifi.WifiManager;
    import android.content.Context;

    // in your class
    private WifiManager wifi;
    wifi =(WifiManager) getApplicationContext().getSystemService(Context.WIFI_SERVICE);
    wifi.setWifiEnabled(true);

