                &lt;/goals&gt;
                &lt;configuration&gt;
                  &lt;rules&gt;
                    &lt;requireMavenVersion&gt;
                      &lt;version&gt;3.8.5&lt;/version&gt;
                    &lt;/requireMavenVersion&gt;                
                  &lt;/rules&gt;
