            for (;;) {
                int rsz = in.read(buffer, 0, buffer.length);
                if (rsz &lt; 0)
                    break;
                out.append(buffer, 0, rsz);
            }
        }
        catch (UnsupportedEncodingException ex) {
            /* ... */
        }
        catch (IOException ex) {
            /* ... */
        }
        return out.toString();
    }

