        new FooEntity(2L, 100L, &quot;foo1&quot;, &quot;bar2&quot;),
        new FooEntity(3L, 100L, &quot;foo2&quot;, &quot;bar3&quot;),
        new FooEntity(4L, 100L, &quot;foo2&quot;, &quot;bar4&quot;),
        new FooEntity(5L, 100L, &quot;foo2&quot;, &quot;bar5&quot;),
