private Bar getBarFromBOrDefault(List&lt;B&gt; listOfB, Id id) {
    return listOfB.stream()
        .filter(b -&gt; b.getIdentity() == id) // or use .equals() here if it&#39;s not a primitive type
        .findFirst()
