        private LocalDateTime creationDate;
    
        @ManyToMany
        @JoinTable(
                name = &quot;tree_node&quot;,
                joinColumns = @JoinColumn(name = &quot;tree_id&quot;),
                inverseJoinColumns = @JoinColumn(name = &quot;node_id&quot;)
        )
        private List&lt;Node&gt; nodes;
    }

