    object JsonParser {
    
      def parse(map: Map[String, Any]): JsValue = {
        val values = for((key, value) &lt;- map) yield {
          value match {
            case m: java.util.Map[String, _] @unchecked =&gt; Json.obj(key -&gt; parse(m.toMap))
            case m: Map[String, _] @unchecked =&gt; Json.obj(key -&gt; parse(m))
