    @OneToMany(fetch = FetchType.LAZY, cascade = CascadeType.ALL)
    @JoinColumn(name = &quot;PRIMARY_KEX_IN_EXCLUSION_TABLE&quot;, referencedColumnName = &quot;id&quot;)
