  Mono&lt;Void&gt; set(String table, String key, Object value, Long ttl, TimeUnit timeUnit);

  &lt;T&gt; Mono&lt;T&gt; get(String table, String key, TypeReference&lt;T&gt; type);

  Mono&lt;Boolean&gt; delete(String table, String key);
}
