    mvn install:install-file &quot;-DgroupId=org.apache.poi&quot; &quot;-DartifactId=poi-ooxml&quot; &quot;-Dpackaging=jar&quot; &quot;-Dfile={your_path_to_jar_file}&quot; &quot;-Dversion=1.1.0&quot;

