    public String deDup(String s) {
        return new LinkedHashSet&lt;String&gt;(Arrays.asList(s.split(&quot;-&quot;))).toString().replaceAll(&quot;(^\\[|\\]$)&quot;, &quot;&quot;).replace(&quot;, &quot;, &quot;-&quot;);
