    int max = 0;
    int indexOfSwitch = 0;
    int diff = 0;

    while(roadIndex1 &gt;=0 &amp;&amp; roadIndex2 &gt;=0)
    {
      diff = Math.abs(totalSumRoad2 - totalSumRoad1);
      if(diff &gt; max)
