    }

    private static int getlb(int pos, int[] a) {
        while (pos - 1 &gt;= 0 &amp;&amp; a[pos] == a[pos - 1]) pos--;
        return pos - 1;
    }

    private static int getUb(int pos, int[] a) {
        while (pos + 1 &lt; a.length &amp;&amp; a[pos] == a[pos + 1]) pos++;
        return pos + 1;

    }
}
