private static final ExecutorService execSvc = Executors.newFixedThreadPool(2);

private static &lt;T, R&gt; List&lt;R&gt; parallelProcess(List&lt;List&lt;T&gt;&gt; llt, Function&lt;List&lt;T&gt;, R&gt; processor) {

    final List&lt;Future&lt;R&gt;&gt; lfr =
