            logConfigManager.setLogLevel(&quot;a.b.c&quot;, &quot;info&quot;);
            logger.debug(&quot;Marvel-3&quot;);
            // Flush everything
            LogManager.shutdown();
    
            String content = Files.readAllLines(file.toPath()).stream().reduce((s1, s2) -&gt; s1 + &quot;\t&quot; + s2).orElse(null);
