    class DateTimeTester {
      ---
      public static void main(String[] args) {
        DateTimeFormatter formatter = DateTimeFormatter.ofPattern(&quot;+yyyy-MM-dd&#39;T&#39;HH:mm:ss&#39;Z&#39;&quot;)
