```java
public class Foo {

    public static void main(String... args) throws IOException {
        Scanner scan = new Scanner(System.in);
        Path file = Paths.get(&quot;Pro-1_Example.txt&quot;);
        // using NIO to easily read all lines from the text file
        List&lt;String&gt; lines = Files.readAllLines(file);

        // using special collection for unique ordered elements
        Set&lt;String&gt; setA = new LinkedHashSet&lt;&gt;();
        Set&lt;String&gt; setB = new LinkedHashSet&lt;&gt;();
        splitLines(lines, setA, setB);

        // using Scanner for user input
        System.out.print(&quot;Enter the exponent k: &quot;);
        int k = scan.nextInt();

        System.out.println(&quot;A ∪ B = &quot; + union(setA, setB));
        System.out.println(&quot;A ◦ B = &quot; + concat(setA, setB));
        System.out.format(&quot;A^%s = %s\n&quot;, k, power(setA, k));
    }

    private static void splitLines(List&lt;String&gt; lines, Set&lt;String&gt; setA, Set&lt;String&gt; setB) {
        boolean readA = true;

        for (String line : lines) {
            if (line.isEmpty())
                readA = false;
            else if (readA)
                setA.add(line);
            else
                setB.add(line);
        }
    }

    // use Set to keep elements unique
    private static Set&lt;String&gt; union(Set&lt;String&gt; setA, Set&lt;String&gt; setB) {
        Set&lt;String&gt; res = new LinkedHashSet&lt;&gt;(setA);
        res.addAll(setB);
        return Collections.unmodifiableSet(res);
    }

    private static Set&lt;String&gt; concat(Set&lt;String&gt; setA, Set&lt;String&gt; setB) {
        Set&lt;String&gt; res = new LinkedHashSet&lt;&gt;();

        for (String a : setA)
            for (String b : setB)
                res.add(a + b);

        return Collections.unmodifiableSet(res);
    }

    private static Set&lt;String&gt; power(Set&lt;String&gt; set, int k) {
        Set&lt;String&gt; res = new LinkedHashSet&lt;&gt;(set);

        for (int i = 1; i &lt; k; i++) {
            List&lt;String&gt; tmp = new ArrayList&lt;&gt;();

            for (String e : set)
                for (String abc : res)
                    tmp.add(abc + e);

            res.addAll(tmp);
        }

        return Collections.unmodifiableSet(res);
    }

}
