      @SuppressWarnings({&quot;CloneReturnsClassType&quot;, &quot;UseOfClone&quot;})
      @Override
      public Object clone() {
        return super.clone();
      }
      
      @FunctionalInterface
      private interface BiTextFunction {
        int loc(JTextComponent component, int position) throws BadLocationException;
      }
    }

