    &lt;property name=&quot;connection.url&quot;&gt;jdbc:mysql://localhost:3306/test?serverTimezone=UTC&lt;/property&gt;
    &lt;property name=&quot;hibernate.default_schema&quot;&gt;test&lt;/property&gt;

