    public CommandLine addArgument(String argument, boolean handleQuoting) {
        if (argument == null) {
            return this;
        } else {
            if (handleQuoting) {
                StringUtils.quoteArgument(argument);
            }

            this.arguments.add(new Argument(argument, handleQuoting));
            return this;
        }
    }
```

