    boolean almostIncreasingSequence(int[] sequence) {
        int removed = 0;

        for (int i = 0; i &lt; sequence.length - 2 &amp;&amp; removed &lt;= 2; i ++) {
            int a = sequence[i];
            int b = sequence[i+1];
            int c = sequence[i+2];
        
            if (a &gt;= b) {
                removed++;
                sequence[i] = b -1;
            } 

            if (b &gt;= c){
                removed++;

                if (a == c) {
                    sequence[i+2] = b +1;
                } else {
                    sequence[i+1] = a;
                }
            }
        }

        return removed &lt;= 1;
