            {
                if (list.get(i+1)&gt;list.get(i-1)) 
                {
                    list.remove(i); 
                }
                else
                {
                    list.remove(i+1);
                }
                break;
            } 
        }

        for (int i =0;i&lt;list.size()-1 ;i++ ) 
        {
            if (list.get(i+1)&lt;list.get(i) || list.get(i+1)==list.get(i) ) 
            {
                increase = false;
            }    
        }
        return increase;
    }

