        System.out.println(&quot;NULL&quot;);
    }
    
    public void RemoveNthNode(int nth){
        if(head == null || head.next==null){
            head = null;
            return;
        }
        int size=0;
        Node curNode=head;
        while(curNode!=null) {
            curNode = curNode.next;
            size++;
        }

        if(nth==size){
            head = head.next;
            return;
        }
         
        Node prevnode = head;
        int i=1;
        while(i&lt;size-nth){
            prevnode=prevnode.next;
            i++;
        }
        prevnode.next = prevnode.next.next;
    }
    
    public static void main(String[] args) {
        Main ll = new Main();
        ll.addFirst(90);
        ll.addFirst(40);
        ll.addFirst(45);
        
        ll.PrintLL();
      
        ll.RemoveNthNode(3);
        ll.PrintLL();
    }
```

