    public static &lt;L, M, R&gt; Optional&lt;Triple&lt;L, M, R&gt;&gt; product(Optional&lt;L&gt; left,
            Optional&lt;M&gt; middle, Optional&lt;R&gt; right) {
        return left.flatMap(l -&gt; middle.flatMap(m -&gt; right.map(r -&gt; Triple.of(l, m, r))));
    }
    
