		}
		public void run2() { System.out.println(&quot;B.run2&quot;); }
	}
	static class ClassC {
		public void run1(String msg) {
			System.out.println(&quot;C.run1: &quot; + msg);
		}
		public void run2() { System.out.println(&quot;C.run2&quot;); }
	}
}
