```java
@Component
@AllArgsConstructor
public class JWTFilter extends OncePerRequestFilter {
    private final JWTUtil jwtUtil;
    private final PersonDetailsService personDetailsService;

    @Override
    protected void doFilterInternal(HttpServletRequest request, HttpServletResponse response, FilterChain filterChain)
            throws ServletException, IOException {
        String authHeader = request.getHeader(&quot;Authorization&quot;);

        if (Objects.nonNull(authHeader) &amp;&amp; !authHeader.isBlank() &amp;&amp; authHeader.startsWith(&quot;Bearer &quot;)) {
            String jwt = authHeader.substring(7);

            if (jwt.isBlank()) {
                response.sendError(HttpServletResponse.SC_BAD_REQUEST, &quot;Invalid or expired JWT&quot;);
            } else {
                try {
                    String username = jwtUtil.validateTokenAndGetClaim(jwt);

                    UserDetails userDetails = personDetailsService.loadUserByUsername(username);

                    UsernamePasswordAuthenticationToken authToken =
                            new UsernamePasswordAuthenticationToken(
                                    userDetails, userDetails.getPassword(), userDetails.getAuthorities());

                    if (Objects.isNull(SecurityContextHolder.getContext().getAuthentication())) {
                        SecurityContextHolder.getContext().setAuthentication(authToken);
                    }

                } catch (JWTVerificationException exception) {
                    response.sendError(HttpServletResponse.SC_BAD_REQUEST, &quot;Invalid or expired JWT&quot;);
                    // return;
                    // TODO Temp Fix
                }
            }
        }
        filterChain.doFilter(request, response);
    }
}
