    private static final String ISSUER = &quot;jwt-spring-app&quot;;
    private static final String JWT_SECRET = &quot;JWT-AUTH-SPRING-BACKEND-APP&quot;; //TODO

    public String generateToken(String username) {
        Instant expirationDate = Instant.from(ZonedDateTime.now().plusMinutes(EXPIRATION_MINUTES));
        return JWT.create()
                .withSubject(SUBJECT)
                .withClaim(&quot;username&quot;, username)
                .withIssuedAt(Instant.now())
                .withIssuer(ISSUER)
                .withExpiresAt(expirationDate)
                .sign(Algorithm.HMAC256(JWT_SECRET));
    }

    public String validateTokenAndGetClaim(String token) throws JWTVerificationException {
        JWTVerifier jwtVerifier = JWT.require(Algorithm.HMAC256(JWT_SECRET))
                .withSubject(SUBJECT)
                .withIssuer(ISSUER)
                .build();
        DecodedJWT decodedJWT = jwtVerifier.verify(token); // throws JWTVerificationException
        return decodedJWT.getClaim(&quot;username&quot;).asString();
    }
