    func getPattern(s string) string {
	   res := make([]int, len(s)+1)

	   i := 0
	   j := -1
	   res[0] = -1

	   var patternLength int
	   for i &lt; len(s) {
		   if j == -1 || s[i] == s[j] {
			   i++
			   j++
			   res[i] = j
			   if res[i] == 0 {
				   patternLength++
			   } else {
				   break
			   }
		   } else {
			   j = res[j]
		   }
	   }
	   if patternLength == len(s) {
		   patternLength = 0
	   }
	   return s[:patternLength]
    }


