~~~
@Data
public class PhoneDetails { 
   
    @JacksonXmlProperty(localName = &quot;Iphone&quot;) 
    private Iphone iphoneDetails; 
}

@Data
public class Iphone {

    private String os;
    @JacksonXmlText
    private String iphone;
}

