      public static void main(String[] args) {
        int[][] num = {{43, 2, 21, 100},{1, 2, 4, 54},{5,84,2,1,1},{11,5,3,1}};

        int size = 0;

        int i = 0;
        int j = 0;
        int sum = 0;
        while(true){
            if(i == num.length){
                /**
                 * insert sum into result array here
                 */

                if(i == num.length &amp;&amp; j == num[i-1].length)
                    break;

                sum = 0;
                i = 0;
                j++;
            }

            if(j &lt; num[i].length) {
                sum += num[i][j];
            }
            i++;
        }
    }

