    int[] uniqueRand(int n, int m){
        Random rand = new Random();
        int[] r = new int[n];
        int[] result = new int[n];
        for(int i = 0; i &lt; n; i++){
            r[i] = rand.nextInt(m-i);
            result[i] = r[i];
            for(int j = i-1; j &gt;= 0; j--){
